var synthetics = require('Synthetics');
const log = require('SyntheticsLogger');

const connectorMonitoring = async function () {

    let accessToken;
    const { FIDC_URL, USER, PASSWORD, ADMIN_CLIENT, ADMIN_CLIENT_SECRET, MONITORED_COMPONENT } = process.env

    const validateAccessToken = async function (res) {
        return new Promise((resolve, reject) => {
            if (res.statusCode < 200 || res.statusCode > 299) {
                throw res.statusCode + ' ' + res.statusMessage;
            }

            let responseBody = '';
            res.on('data', (d) => {
                responseBody += d;
            });

            res.on('end', () => {
                // Add validation on 'responseBody' here if required.
                accessToken = JSON.parse(responseBody).access_token;
                resolve();
            });
        });
    };

    const validateConnectorHealth = async function (res) {
        return new Promise((resolve, reject) => {
            if (res.statusCode < 200 || res.statusCode > 299) {
                throw res.statusCode + ' ' + res.statusMessage;
            }

            let responseBody = '';
            res.on('data', (d) => {
                responseBody += d;
            });

            res.on('end', () => {
                const connector = JSON.parse(responseBody)
                if (connector.ok === 'true') {
                    resolve();
                } else {
                    reject(`${connector.name} - state: ${connector.ok}`)
                }
            });
        });
    };


    // Set request option for Get Access Token
    let requestAccessToken = {
        hostname: FIDC_URL.replace('https://', ''),
        method: 'POST',
        path: '/am/oauth2/realms/root/realms/alpha/access_token?auth_chain=PasswordGrant',
        port: '443',
        protocol: 'https:',
        body: `grant_type=password&username=${USER}&password=${PASSWORD}&scope=fr:idm:*&client_id=${ADMIN_CLIENT}&client_secret=${ADMIN_CLIENT_SECRET}`,
        headers: { "Content-Type": "application/x-www-form-urlencoded" }
    };
    requestAccessToken['headers']['User-Agent'] = [synthetics.getCanaryUserAgentString(), requestAccessToken['headers']['User-Agent']].join(' ');

    // Set step config option for Get Access Token
    let configAccessToken = {
        includeRequestHeaders: false,
        includeResponseHeaders: false,
        includeRequestBody: false,
        includeResponseBody: false,
        restrictedHeaders: [],
        continueOnHttpStepFailure: true
    };

    await synthetics.executeHttpStep('Get Access Token', requestAccessToken, validateAccessToken, configAccessToken);

    const connectors = MONITORED_COMPONENT.split(',');

    await Promise.all(connectors.map(async (connector) => {
      // Set request option for Verify Connectors Health
      let requestConnectorHealth = {
        hostname: FIDC_URL.replace('https://', ''),
        method: 'POST',
        path: `/openidm/system/${connector}?_action=test`,
        port: '443',
        protocol: 'https:',
        body: "",
        headers: { "Content-Type": "application/json", "Authorization": `Bearer ${accessToken}` }
      };
      requestConnectorHealth['headers']['User-Agent'] = [synthetics.getCanaryUserAgentString(), requestConnectorHealth['headers']['User-Agent']].join(' ');

      // Set step config option for Connectors Health
      let configConnectorsHealth = {
        includeRequestHeaders: false,
        includeResponseHeaders: false,
        includeRequestBody: false,
        includeResponseBody: false,
        restrictedHeaders: [],
        continueOnHttpStepFailure: true
      };

      return await synthetics.executeHttpStep(`Verify Connector ${connector}`, requestConnectorHealth, validateConnectorHealth, configConnectorsHealth);
    }))

};

exports.handler = async () => {
    return await connectorMonitoring();
};

