var synthetics = require('Synthetics');
const log = require('SyntheticsLogger');

const reconDuration = async function () {

    let accessToken;
    const { FIDC_URL, USER, PASSWORD, ADMIN_CLIENT, ADMIN_CLIENT_SECRET, MONITORED_COMPONENT, RECON_DURATION } = process.env

    const validateAccessToken = async function (res) {
        return new Promise((resolve, reject) => {
            if (res.statusCode < 200 || res.statusCode > 299) {
                throw res.statusCode + ' ' + res.statusMessage;
            }

            let responseBody = '';
            res.on('data', (d) => {
                responseBody += d;
            });

            res.on('end', () => {
                // Add validation on 'responseBody' here if required.
                accessToken = JSON.parse(responseBody).access_token;
                resolve();
            });
        });
    };

    const validateReconDuration = async function (res) {
        return new Promise((resolve, reject) => {
            if (res.statusCode < 200 || res.statusCode > 299) {
                throw res.statusCode + ' ' + res.statusMessage;
            }

            let responseBody = '';
            res.on('data', (d) => {
                responseBody += d;
            });

            res.on('end', () => {
                const mapping = JSON.parse(responseBody).mappings.filter(mapping => mapping.recon)[0]
                if (mapping && mapping.recon.state === 'ACTIVE' && mapping.recon.duration > `${RECON_DURATION}`) {
                    reject(`${mapping.name} - state: ${mapping.recon.state}, duration: ${mapping.recon.duration}`)
                } else {
                    resolve(`${mapping.name} - state: ${mapping.recon.state}`)
                }
            });
        });
    };


    // Set request option for Get Access Token
    const pw = `${PASSWORD}`
    const encodedPassword = encodeURI(pw)
    const clientSecret = `${ADMIN_CLIENT_SECRET}`
    const encodedClientSecret = encodeURI(clientSecret)

    let requestAccessToken = {
        hostname: FIDC_URL.replace('https://', ''),
        method: 'POST',
        path: '/am/oauth2/realms/root/realms/alpha/access_token?auth_chain=PasswordGrant',
        port: '443',
        protocol: 'https:',
        body: `grant_type=password&username=${USER}&password=${encodedPassword}&scope=fr:idm:*&client_id=${ADMIN_CLIENT}&client_secret=${encodedClientSecret}`,
        headers: { "Content-Type": "application/x-www-form-urlencoded" }
    };
    requestAccessToken['headers']['User-Agent'] = [synthetics.getCanaryUserAgentString(), requestAccessToken['headers']['User-Agent']].join(' ');

    // Set step config option for Get Access Token
    let configAccessToken = {
        includeRequestHeaders: false,
        includeResponseHeaders: false,
        includeRequestBody: false,
        includeResponseBody: false,
        restrictedHeaders: [],
        continueOnHttpStepFailure: true
    };

    log.info("full request is: " + JSON.stringify(requestAccessToken));
    log.info("access token config: " + JSON.stringify(configAccessToken));
    await synthetics.executeHttpStep('Get Access Token', requestAccessToken, validateAccessToken, configAccessToken);

    const mappings = MONITORED_COMPONENT.split(',');

    await Promise.all(mappings.map(async (mapping) => {
      // Set request option for Verify Mappings Recon
      let requestReconDuration = {
        hostname: FIDC_URL.replace('https://', ''),
        method: 'GET',
        path: `/openidm/endpoint/mappingDetails?mapping=${mapping}`,
        port: '443',
        protocol: 'https:',
        body: "",
        headers: { "Content-Type": "application/json", "Authorization": `Bearer ${accessToken}` }
      };
      requestReconDuration['headers']['User-Agent'] = [synthetics.getCanaryUserAgentString(), requestReconDuration['headers']['User-Agent']].join(' ');

      // Set step config option for Verify Mappings Recon
      let configReconDuration = {
        includeRequestHeaders: false,
        includeResponseHeaders: false,
        includeRequestBody: false,
        includeResponseBody: false,
        restrictedHeaders: [],
        continueOnHttpStepFailure: true
      };

      log.info("full request is: " + JSON.stringify(requestReconDuration));
      return await synthetics.executeHttpStep(`Verify Mapping ${mapping}`, requestReconDuration, validateReconDuration, configReconDuration);
    }))

};

exports.handler = async () => {
    return await reconDuration();
};

